
public class Ex2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num3=5;
		
		num3++;
		System.out.println(num3);
		System.out.println(num3++); //출력이 먼저++는 나중에 계산된다
		System.out.println(++num3);
		
		--num3;
		System.out.println(num3);
		System.out.println(num3--);
		System.out.println(--num3);
	}

}
